  if n=0 then 0
  else n+ sum(n-1);;

2.2_fib.solution:
let rec fibo n =
  match n with 
  |0->1
  |1->1 
  |n-> fibo(n-1)+fibo(n-2);;

fibo 10;;

2.3_employe.solution:
let rec pasA n =
  if n=0 then 0
  else 1+pasB(n-1)
and pasB n =
  if n=0 then 0
  else 
  if n mod 2=0 then 1+pasA(n-2)
  else 1+pasA (n-1);;

2.4_ack.solution:
let rec ack m n =
  match(m,n) with
  |(0,n)-> n+1
  |(m,0)-> ack(m-1)(1)
  |(m,n)-> ack(m-1)(ack (m)(n-1));;

ack 0 0;
ack 1 1;
ack 2 2;

3.1_listes.solution:
(* Toutes les fonction sont ici dfinies par des "let rec". Il est
   possible que dans vos solutions certaines fonctions ne soient pas
   rcursives, car elles utilisent une fonction recursive auxiliaire
   dfinie localement.  vous d'enlever le mot clef "rec" dans ce
   cas. *)

let rec list_length l =
  match l with
  |[]->0
  |_::l->1+list_length l;;

list_length[2;2;3];;

let rec list_product l =
  match l with 
  |[]->1
  |x::l->x*list_product l;;

list_product [2; 2; 3];;

let rec mem x l =
  match l with 
  |[]-> false
  |e::l-> 
      if x=e then true 
      else mem x l;;


let rec list_min l =
  match l with 
  |[]-> failwith "erreur, liste vide"
  |[x]->x
  |x::l-> min(x)(list_min l);;

list_min [-30; 2; 549];;

let rec last l =
  match l with 
  |[]-> failwith"liste vide"
  |[x]-> x
  |_::l->last l;;
last [1; 2; 3];;

let rec is_sorted l =
  match l with 
  |[]->true
  |[e1]->true
  |e1::e2::l-> if e1>e2 then false 
      else is_sorted (e2::l);;

is_sorted [1; 3; 5; 6; 4];;
is_sorted[1;2;3;4];;




let average l =
  let rec somme l=
    match l with
    |[]-> failwith"erreur"
    |[x]->x
    |x::l->x+somme l 
  in 
  somme l / list_length l;;




let rec nth l k =
  match l with 
  |[]-> failwith "vide"
  |x::l->if k=0 then x
      else nth (l)(k-1);;


let rec range n m =
  if n=m then n::[]
  else 
  if n<m then 
    n::range(n+1) (m)
  else  
    n::range(n-1)(m);;

3.2_listes_poly.solution:
(* Toutes les fonctions sont ici dfinies par des "let rec". Il est
   possible que dans vos solutions certaines fonctions ne soient pas
   rcursives, car elles utilisent une fonction rcursive auxiliaire
   dfinie localement.  vous d'enlever le mot clef "rec" dans ce
   cas. *)

let rec map f l =
  match l with 
  |[]->[] 
  |x::l-> f(x)::map f l;;
let succ x = x + 1 in map succ [1; 2; 3];;

let rec filter p l =
  match l with 
  |[]->[]
  |x::l when p(x)-> x:: filter p l
  |x::l -> filter p l;;
filter (function x -> x > 3) [4; 3; 10];;

let rec append l1 l2 =
  match l1 with 
  |[]->l2
  |x::r1-> x::append r1 l2 ;;

let rec rev l =
  match l with 
  |[]->[] 
  |x::l-> rev l @ [x];;

rev [1; 2; 3];;


let rec flatten l =
  match l with 
  |[]-> []
  |h::t -> append h (flatten t);;


let rec rotation_d l=
  let rec last l=
    match l with 
    |[]->[]
    |[x]->[x]
    |_::r-> last r in 
  let rec remove_last l=
    match l with 
    |[]->[]
    |[x]->[]
    |x::r->x::remove_last r in 
  let last= last l in 
  last@remove_last l;;

