4.1_arbres.solution:
let rec size a =
  match a with
  |Nil -> 0
  |Node(_,a1,a2)->1+size a1+size a2;;

let rec depth a =
  match a with
  |Nil->0 
  |Node(_,a1,a2)->1+max(depth a1)(depth a2);;

let rec sum a =
  match a with 
  |Nil-> 0
  |Node(e,a1,a2)->e+sum a1 + sum a2;;

let rec contains x a =
  match a with 
  |Nil->false
  |Node(e,a1,a2) when e=x->true
  |Node(e,a1,a2)-> contains x a1 || contains x a2;;

let rec elements a =
  match a with
  |Nil-> []
  |Node(e,a1,a2)->elements a1 @ [e] @ elements a2;;

let rec elements a =
  let rec append l1 l2 =
    match l1 with 
    |[]->l2
    |x::r1-> x::append r1 l2 in 
  match a with 
  |Nil-> []
  |Node(e,a1,a2)->append (append (elements a1)( [e]))(elements a2);;

let rec perfect a =
  match a with 
  |Nil -> true 
  |Node (_,a1,a2) when depth a1=depth a2 -> perfect a1 && perfect a2 
  |Node(_,a1,a2)-> false;;

let perfect1 a =
  let rec perfect_depth a=
    match a with 
    |Nil->(true,0)
    |Node(_,a1,a2)-> 
        let pf1 = perfect_depth a1 in
        let pf2 = perfect_depth a2 in 
        (snd pf1 = snd pf2 && fst pf1 && fst pf2, 1+ max(snd pf1)(snd pf2))
  in fst(perfect_depth (a)) ;;

4.3_arbres_poly.solution:
let rec forall_labels p a =
  match a with
  |Nil ->true
  |Node(n,g,d) when p n -> forall_labels p g && forall_labels p d
  |Node (n,g,d)->false;;

let is_uniform x a =
  forall_labels (fun e-> e=x) a;;

let rec forall_subtrees pn a =
  match a with
  |Nil->true
  |Node(n,g,d)-> if pn n g d then forall_subtrees pn g && forall_subtrees pn d
      else false;;

let is_right_comb a =
  match a with 
  |Nil->true
  |Node(x,g,d)->forall_subtrees (fun x g d-> 
      if g=Nil then true 
      else false) a;;

let sum a =
  fold_tree (fun x y z-> x+y+z) 0 a;;




let map_tree f a =
  let rec fold fn vf a =
    match a with
    | Nil -> vf
    | Node (n, g, d) -> Node (fn n, (fold fn vf g), (fold fn vf d))in 
  fold (fun e-> (f e)) Nil a;;

5.1_calcul_prop.solution:
let rec string_of_formula f =
  match f with 
  |Prop p-> p
  |Neg (phi)->"Neg "^ string_of_formula phi
  |And(phi1,phi2)->"("^ string_of_formula phi1 ^" And "^string_of_formula phi2^ ")"
  |Or(phi1,phi2)->"("^ string_of_formula phi1 ^" Or "^string_of_formula phi2^ ")";;
string_of_formula (And (Prop "p", Neg (Or (Prop "q", Prop "r"))));;
string_of_formula (Or (Prop "a", Prop "b"));;

let rec list_of_props f =


  match f with 
  |Prop p-> [p]
  |Neg(phi1)->list_of_props phi1
  |And(phi1,phi2)->union_sorted (list_of_props phi1)(list_of_props phi2)
  |Or(phi1,phi2)->union_sorted (list_of_props phi1)(list_of_props phi2);;


let rec eval_formula f l =
  match f with 
  |Prop p->(match List.assoc p l with 
      |false->false
      |true->  true
    )
  |Neg phi->not(eval_formula phi l)
  |And(phi1,phi2)->  (eval_formula phi1 l && eval_formula phi2 l)
  |Or(phi1,phi2)->  (eval_formula phi1 l || eval_formula phi2 l);;

5.2_formules_satisfiables.solution:
let add_to_all x ll =
  List.map (fun l -> x::l) ll;;

let rec interpretations_props l =
  match l with 
  |[]->[[]] 
  |p::l-> let a= interpretations_props l in (add_to_all (p,false) a) @ (add_to_all (p,true) a) ;;


let interpretations f = 
  interpretations_props (list_of_props f) ;;


let satisfiable f =
  let inter= interpretations f in
  List.exists(eval_formula f) (inter);;

let tautology f =
  let inter= interpretations f in
  List.for_all(eval_formula f) (inter);;

