let spiral size factor angle n = let l = List.init (2*n) (fun i-> Line size) in
  List.mapi (fun i -> fun x -> if i mod 2 = 0 then
                (Line (size *. (Float.pow factor (float_of_int (i/2)))))
              else (Turn angle)
            ) l

